import React from "react";
import { MuiThemeProvider, Grid } from "@material-ui/core";
import Paper from "@material-ui/core/Paper";
import Input from "@material-ui/core/Input";
import Button from "@material-ui/core/Button";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  item: {
    marginTop: 30,
    marginBottom: 30
  },
  form: {
    maxWidth: 600,
    marginTop: 30,
    visibility: "hidden"
  }
});

function LoginAndReg() {
  const classes = useStyles();

  const [value, setValue] = React.useState("");
  const [logInStyle, setLogInStyle] = React.useState("hidden");
  const [signUpStyle, setSignUpStyle] = React.useState("hidden");

  const handleRadioClick = event => {
    setValue(event.target.value);
    if (value === "logIn") {
      setLogInStyle("visible");
      setSignUpStyle("hidden");
    } else if (value === "signUp") {
      setSignUpStyle("visible");
      setLogInStyle("hidden");
    }
  };

  return (
    <MuiThemeProvider>
      <Grid container justify="space-around">
        <h1>Log in or if you new sign up for the YourTime!</h1>
      </Grid>
      <Grid container justify="space-around">
        <Button
          variant="outlined"
          color="primary"
          value="logIn"
          onClick={handleRadioClick}
        >
          Log In
        </Button>
        <Button
          variant="outlined"
          color="secondary"
          value="signUp"
          onClick={handleRadioClick}
        >
          Sing Up
        </Button>
      </Grid>
      <Grid container spacing={10} justify="space-around">
        <Grid
          item
          xs={12}
          sm={6}
          className={classes.form}
          style={{ visibility: `${logInStyle}` }}
        >
          <Paper>
            <Grid container justify="space-around">
              <h1>Log in</h1>
            </Grid>
            <form>
              <Grid container justify="space-around">
                <Input
                  className={classes.item}
                  required
                  placeholder="Username"
                  type="text"
                />
                <Input
                  className={classes.item}
                  required
                  placeholder="Password"
                  type="password"
                />
                <Input
                  className={classes.item}
                  required
                  placeholder="ID"
                  type="text"
                />
                <Grid container justify="space-around" className={classes.item}>
                  <Button variant="contained" color="primary">
                    Submit
                  </Button>
                </Grid>
              </Grid>
            </form>
          </Paper>
        </Grid>
        <Grid
          item
          xs={12}
          sm={6}
          className={classes.form}
          style={{ visibility: `${signUpStyle}` }}
        >
          <Paper>
            <Grid container justify="space-around">
              <h1>Sign up</h1>
            </Grid>
            <form>
              <Grid container justify="space-around">
                <Input
                  className={classes.item}
                  required
                  placeholder="Username"
                  type="text"
                />
                <Input
                  className={classes.item}
                  required
                  placeholder="Password"
                  type="password"
                />
                <Input
                  className={classes.item}
                  required
                  placeholder="ID"
                  type="text"
                />
                <Grid container justify="space-around" className={classes.item}>
                  <Button variant="contained" color="primary">
                    Submit
                  </Button>
                </Grid>
              </Grid>
            </form>
          </Paper>
        </Grid>
      </Grid>
    </MuiThemeProvider>
  );
}

export default LoginAndReg;
